import { Component } from 'react';
import  './App.css';
import { CardList } from './components/card-list/card-list.component';

class App extends Component{
  constructor(){
super();
this.state = {
  monster: [],
  searchField: ''
}
  }
  componentDidMount(){
    fetch('https://jsonplaceholder.typicode.com/users')
    .then(Response => Response.json())
    .then(users => this.setState({ monster : users}))
  }
  render(){
    const { monster, searchField} = this.state;
    const filterMonster = monster.filter(monster => monster.name.toLowerCase().includes(searchField.toLocaleLowerCase())
      );
    return(
      <div className="App">
     <input type='search' placeholder="search monster" onChange={e => this.setState({searchField:e.target.value})}/>
     <CardList monster = {filterMonster}/>

    </div>
    )
  }
}

export default App;
