import { Component } from 'react';

class App extends Component{
  constructor(){
super();
this.state = {
  monster: [
    {
      name: 'frankenstain'
    },
    {
      name: 'dracula'
    },
    {
      name: ''
    },
  ]
}
  }
  render(){
    return(
      <div className="App">
      <header className="App-header">
        <p>{this.state.String}</p>
        <button onClick={() => this.setState({String:"hello boy" })}> Change the text</button>

      </header>
    </div>
    )
  }
}

export default App;
